import type { CollectibleType, ParentArea } from "$/lib/types/types";
import { get, writable } from 'svelte/store';

function useNewGameStore() {
	const collectibleTypes = writable<CollectibleType[]>([
		{
			id: 0,
			title: 'Cica',
			description: 'Mica',
			image: undefined,
			imageSrc:
				'data:image/jpeg;base64,'
		},
		{
			id: 1,
			title: 'Mica',
			description: 'cica',
			image: undefined,
			imageSrc: ''
		},
		{
			id: 2,
			title: 'Test',
			description: 'amentum',
			image: undefined,
			imageSrc: ''
		}
	]);

	const areas = writable<ParentArea[]>([
		{
			id: 1000,
			title: 'Area 1',
			description: 'Super area',
			image: undefined,
			imageSrc:
				'data:image/jpeg;base64,',
			children: [
				{
					id: 1001,
					title: 'Area 1.1',
					description: 'Subarea',
					image: undefined,
					imageSrc: '',
				}
			],
		},
		{
			id: 2000,
			title: 'Area 2',
			description: 'Lesser but still super area',
			image: undefined,
			imageSrc: '',
			children: [],
		}
	]);

	function getCollectibleTypeById(id: number): CollectibleType {
		return get(collectibleTypes).find(collectibleType => collectibleType.id === id) as CollectibleType;
	}

	return {
		collectibleTypes,
		areas,
		getCollectibleTypeById
	};
}

export const NewGameStore = useNewGameStore();
